import React, { useState } from "react";
import ExpenseDate from "./ExpenseDate";
import "./ExpenseItem.css";

const ExpenseItem = (props) => {
  const [title, setTitle] = useState(props.title);

  function clickHandle() {
    setTitle("Updated");
  }
  return (
    <div className="mb-4 mt-2">
      <div className="expenseItem m-2 rounded ">
        <div className="ms-2 d-flex text-light">
          <ExpenseDate month="August" year={2022} day={22} />
          <h2 className="me-auto mt-2 ms-3 title">{title}</h2>
          <div className="py-2 px-3 price  me-4 rounded fs-5 border border-white">
            {props.price}
          </div>
          <button className=" btn btn-primary" onClick={clickHandle}>
            Update
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExpenseItem;
